import React, {Component} from "react";
import Products from "./component/Products";
import { ProductService } from "./service/ProductService";
import { Panel } from 'primereact/panel';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';

import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

export default class App extends Component{
  constructor(){
    super();
    this.state = {};
    //this.setState({
    //  product: []
    //});
    this.productService = new ProductService();

    };  

  //componentDidMount(){
  //  this.productService.getAll().then(data => {
  //    console.log(data);
  //  })
  //}

  componentDidMount(){
    this.productService.getAll().then(data => this.setState({product: data}))
    //this.productService.getAll().then(data => this.setProducto({product: data}))
  };

  render(){
    return(
      <div>
        <Panel header="Product CRUD App">
          <DataTable value={this.state.product} selectionMode="single" selection={this.state.selectionProduct} onSelectionChange={e => this.setState({ selectionProduct: e.value })}>
            <Column field="name" header="NAME"></Column>
            <Column field="productType" header="PRODUCTTYPE"></Column>
            <Column field="image" header="IMAGE"></Column>

          </DataTable>


        </Panel>

      </div>
    );
  };

  //<Products productos = {this.state}/>
}
