//import React = require("react");
import React, { Component } from 'react';
import { ProductService } from "../service/ProductService";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Panel } from 'primereact/panel';
import { Button } from 'primereact/button';
import { Ripple } from 'primereact/ripple';


export default class Products extends Component {
    constructor() {
        super();
        this.state = {
            first1: 0,
            rows1: 10
        };
        //this.setState({
        //  product: []
        //});
        this.productService = new ProductService();
        this.mensaje = this.devolverMensaje.bind(this);
        this.onCustomPage1 = this.onCustomPage1.bind(this);



    };

    onCustomPage1(event) {
        this.setState({
            first1: event.first,
            rows1: event.rows,
            currentPage: event.page + 1
        });
    }

    devolverMensaje() {
        console.log(this.props.mensaje)
    };

    componentDidMount() {
        this.productService.getAll().then(data => this.setState({ product: data }))
        //this.productService.getAll().then(data => this.setProducto({product: data}))
    };

    render() {
        const template1 = {
            layout: 'PrevPageLink PageLinks NextPageLink RowsPerPageDropdown CurrentPageReport',
            'PrevPageLink': (options) => {
                return (
                    <button type="button" className={options.className} onClick={options.onClick} disabled={options.disabled}>
                        <span className="p-p-3">Previous</span>
                        <Ripple />
                    </button>
                )
            },
            'NextPageLink': (options) => {
                return (
                    <button type="button" className={options.className} onClick={options.onClick} disabled={options.disabled}>
                        <span className="p-p-3">Next</span>
                        <Ripple />
                    </button>
                )
            },
            'PageLinks': (options) => {
                if ((options.view.startPage === options.page && options.view.startPage !== 0) || (options.view.endPage === options.page && options.page + 1 !== options.totalPages)) {
                    const className = classNames(options.className, { 'p-disabled': true });

                    return <span className={className} style={{ userSelect: 'none' }}>...</span>;
                }

                return (
                    <button type="button" className={options.className} onClick={options.onClick}>
                        {options.page + 1}
                        <Ripple />
                    </button>
                )
            },
            'RowsPerPageDropdown': (options) => {
                const dropdownOptions = [
                    { label: 10, value: 10 },
                    { label: 20, value: 20 },
                    { label: 50, value: 50 },
                    { label: 'All', value: options.totalRecords }
                ];

                return <Dropdown value={options.value} options={dropdownOptions} onChange={options.onChange} appendTo={document.body} />;
            },
            'CurrentPageReport': (options) => {
                return (
                    <span className="p-mx-3" style={{ color: 'var(--text-color)', userSelect: 'none' }}>
                        Go to <InputText size="2" className="p-ml-1" value={this.state.currentPage} tooltip={this.state.pageInputTooltip}
                            onKeyDown={(e) => this.onPageInputKeyDown(e, options)} onChange={this.onPageInputChange}/>
                    </span>
                )
            }
        };
        return (
            <Panel header="Product CRUD App" style={{width:'80%', margin: '0 auto', marginTop: '20px'}}>
                <DataTable value={this.state.product} paginator={true} rows="4">
                    <Column field="name" header="NAME"></Column>
                    <Column field="productType" header="PRODUCTTYPE"></Column>
                    <Column field="image" header="IMAGE"></Column>
                </DataTable>
                <Button onClick = {this.mensaje} label="Success" className="p-button-success"/>
            </Panel>

        );
    }

}



